import React from 'react'

const Nav = props => {
    const [scrolled, setScrolled] = useState(false);
    const navRef = use();
    navRef.current = scrolled;
    useEffect(() => {
        const handleScroll = () => {
            const show = window.scrollY > 150;
            if (show) {
                setScrolled(true)
            } else {
                setScrolled(false);
            }
        }
        document.addEventListener('scroll', handleScroll)
        return () => {
            document.removeEventListener('scroll', handleScroll)
        }
    }, []);
    return (
        <nav className={`d-flex justify-between align-center ${scrolled ? 'scrolled' : ''}`}>
            <div className="logo">
                wood<span>Land</span>
            </div>
            <div className="nav-menu">
                <div className="menu-items d-flex justify-between align-center flex-wrap">
                    <div className="menu-item">
                        Who we
                    </div>
                    <div className="menu-item">
                        Furniture
                    </div>
                    <div className="menu-item">
                       Accesories
                    </div>
                    <div className="menu-item">
                        Know Woods
                    </div>
                    <div className="menu-item">
                        Shop Now
                    </div>
                </div>
            </div>
            <div className="nav-account d-flex justify-between align-center flex-wrap">
                <div className="nav-search d-flex align-center row-reverse">
                    <input type="text" name="search_key" placeholder="Search.." className="search_text" />
                    <i className="material-icons search-btn" aria-hidden="true">search</i>
                </div>
                <div className="nav-profile">
                    <i className="material-icons">perm_identity</i>
                </div>
                <div className="nav-cart">
                    <i className="material-icons">local_mall</i>
                </div>
                <div className="nav-logout">
                    <i className="material-icons">exit_to_app</i>
                </div>
            </div>
        </nav>
    )
}

export default Nav
